import React from 'react'
import './Card.css'

let onImageClick = (url) => {
    console.log(url)
    return (<div style={{ width: "100px" }}><img src={url} alt="Avatar" style={{ width: "500px" }}></img></div>)
}

const Card = (props) => {
    return (
        <>
            <div className="card">
                <div className="image">
                    <img src={props.url} alt="Avatar" style={{ width: "100%", height: "150px" }} onClick={() => onImageClick(props.url)}></img>
                </div>
                <div className="container">
                    <div>
                        <div>
                            {props.likes}
                            <div onClick={() => props.any(props.id)}>
                                {props.liked ? "Unlike" : "Like"}
                            </div>
                        </div>
                        <p>{props.category}</p>
                    </div>
                    <div style = {{height:"20px", width:"100%", backgroundColor:"wheat"}}>
                    <div style = {{height:"20px", width:"100%", backgroundColor:"violet"}}>Post </div>
                    </div>
                    <div style={{ borderTop: "1px solid black" }}>{props.comments.map((item) => <div style={{ backgroundColor: "yellow", borderBottom: "1px solid black" }}>{item}</div>)}</div>
                </div>
            </div>
        </>
    )
}
export default Card;
