import React, { Component } from 'react'
import Card from '../../Components/Card/Card'
import './AllCardsPage.css'

class AllCardsPage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            picsData: [],
            newComment:""
        }
    }

    componentDidMount() {
        fetch("https://raw.githubusercontent.com/Lokenath/MyRepo/master/Test/package.json")
            .then(response => response.json())
            .then(data => this.setState({ picsData: data.pics }));
    }

    any = (id) => {
        let newData = this.state.picsData.map(item => {
            if (id === item.id && item.liked !== true) {
                let currentItem = item
                currentItem.likes = item.likes + 1
                currentItem.liked = true
                return currentItem
            }
            if (id === item.id && item.liked === true) {
                let currentItem = item
                currentItem.likes = item.likes - 1
                currentItem.liked = false
                return currentItem
            }
            else {
                return item;
            }
        })



        this.setState(prevState => {
            return {
                ...prevState,
                picsData: newData
            }
        })
    }

    onCommentChange = (event, id) => {
        this.setState({newComment:event.target.value})
    }

    onPostClicked = (id, comment) => {
        let newData = this.state.picsData.map(item => {
            if (id === item.id) {
                let currentItem = item
                item.comments.push(comment)
                currentItem = item
                return currentItem
            }
            else {
                return item;
            }
        })
        if (this.state.picsData !== newData){
            this.setState({picsData: newData})
        }
        

        // console.log(this.state.picsData)
    }

    render() {
        return (
            <div className="Cards">
                {this.state.picsData.map(item => <Card value={this.state.newComment} onPostClicked = {this.onPostClicked} onCommentChange = {this.onCommentChange} any={this.any} {...item} />)}
            </div>
        )
    }
}

export default AllCardsPage;
