import React, { useState } from 'react';
import './Card.css'

let onImageClick = (url) => {
    console.log(url)
    return (<div style={{ width: "100px" }}><img src={url} alt="Avatar" style={{ width: "500px" }}></img></div>)
}

const Card = (props) => {
    const [comment, setComment] = useState("");
    return (
        <>
            <div className="card">
                <div className="image">
                    <img src={props.url} alt="Avatar" style={{ width: "100%", height: "150px" }} onClick={() => onImageClick(props.url)}></img>
                </div>
                <div className="container">
                    <div className="picture-subInfo">
                        <div className="like-styles">
                            {props.likes}
                            <div onClick={() => props.any(props.id)}>
                                {props.liked ? "Unlike" : "Like"}
                            </div>
                        </div>
                        <div className="categoryTypeStyles">{props.category}</div>
                    </div>
                    <div className="CommentBox">
                        <input className="CommentText" type="text" value={comment} onChange = {(event) => setComment(event.target.value)} />
                        <div className="CommentButton" onClick = {() => props.onPostClicked(props.id, comment)} >Post </div>
                    </div>
                    <div className="comments">{props.comments.map((item) => <div style={{ backgroundColor: "yellow", borderBottom: "1px solid black" }}>{item}</div>)}</div>
                </div>
            </div>
        </>
    )
}
export default Card;
